const inputStrategy = () => {
    const strategy = {
        notNull(value) {
            return /\S+/.test(value) ? '' : '';
        },
        number(value) {
            return /^[0-9]$/.test(value) ? '' : '';
        },
        phone(value) {
            return /^\d{3}\-\d{8}$|^\d{4}\-\d{7}$/.test(value) ? '' : ''
        }
    }

    return {
        check( type, value ) {
            value = value.trim();
            return strategy[type] && strategy[type](value);
        },
        addStrategy(type, fn) {
            strategy[type] = fn;
        }
    }
}
